import { cleanup } from "axe-core";
import React, { useState ,useEffect} from "react";
import { Link } from "react-router-dom";
import { isAuthenticated } from "../auth/helper";

import Base from "../core/Base";

import {getAllCategories} from "./helper/adminapicall"


const AddProducts = () => {

    const {user ,toke } = isAuthenticated();

    const [values , setValues] = useState({
        name:"",
        description:"",
        price:"",
        stock : "",
        photo : "",
        categories:[],
        category:"",
        loading:false,
        error : "",
        createdProduct : "",
        getaRedirect:"",
        formData:""
    });

    const {name , price,description,stock ,categories,category,loading,error,createdProduct,getaRedirect,formData} = values;

    //on submitiing or create product button
    const onSubmit =() => {
        //
    }

    //handling on change in inputs
    const handleChange = name => event => {
        //photo 
        const value = name === "photo" ? event.target.file[0] :event.target.value ;
        formData.set(name,value);
        setValues({...values , [name]:value})
    };

    //pre-loading the data from the database so that it will help in creating product

    const preLoad = () => {
        getAllCategories().then(data => {
            console.log(data);
            if(data.error){
                setValues({...values , error:data.error})
            }
            else{
                setValues({...values , categories:data , formData:new FormData() })
            }
        });
    };

    useEffect ( () => {
        preLoad();
    },[]);


    const createProductForm = () => (
        <form >
          <span>Post photo</span>
          <div className="form-group mb-2 " >
            <label className="btn btn-block btn-success text-center" style={{width:'100%'}}>
              <input
                onChange={handleChange("photo")}
                type="file"
                name="photo"
                accept="image"
                placeholder="choose a file"
              />
            </label>
          </div>
          <div className="form-group mb-1">
            <input
              onChange={handleChange("name")}
              name="photo"
              className="form-control"
              placeholder="Name"
              value={name}
            />
          </div>
          <div className="form-group mb-1">
            <textarea
              onChange={handleChange("description")}
              name="photo"
              className="form-control"
              placeholder="Description"
              value={description}
            />
          </div>
          <div className="form-group mb-1">
            <input
              onChange={handleChange("price")}
              type="number"
              className="form-control"
              placeholder="Price"
              value={price}
            />
          </div>
          <div className="form-group mb-1">
            <select
              onChange={handleChange("category")}
              className="form-control"
              placeholder="Category"
            >
              <option>Select</option>

              {categories && categories.map((cate , index) =>(
                  <option key={index}value={cate._id}>{cate.name}</option>
              ))}

            </select>
          </div>
          <div className="form-group mb-1">
            <input
              onChange={handleChange("quantity")}
              type="number"
              className="form-control"
              placeholder="Quantity"
              value={stock}
            />
          </div>

          <button type="submit" onClick={onSubmit} className="btn btn-outline-success mb-3 mt-2">
            Create Product
          </button>
        </form>
      );


    return (
        <Base title="Add Product here" description="welcome to product creation section" className="container bg-success p-4">
        <Link  to ="/admin/dashboard" className="btn btn-md btn-success mb-2">Admin Home</Link>
        <div className="row bg-dark text-white rounded">
            <div className="col-md-8 offset-md-2">
                {createProductForm()}
            </div>
        </div>
        </Base>
    )
}


export default AddProducts;
