import React from  "react";
import ImageHelper from "./helper/ImageHelper";



    const Card = ({product  ,addtoCart =true,removeFromcart=false}) => {

       const showAddtoCart =(addtoCart) => {
           return(
               addtoCart && (
                <button
                onClick={() => {}}
                className="btn btn-block btn-outline-success  mb-2" style={{width:"100%"}}
              >
                Add to Cart
              </button>
               )
           )
       }

       const showRemoveFromCart =(removeFromcart) => {
        return(
            <button
                    onClick={() => {}}
                    className="btn btn-block btn-outline-danger  " style={{width:"100%"}}
                  >
                    Remove from cart
                  </button>
        )
    }


        return (
          <div className="card text-white bg-dark border border-info ">
            <div className="card-header lead">A photo from pexels</div>
            <div className="card-body">
              <ImageHelper product={product}/>
              <p className="lead bg-success text-center font-weight-normal text-wrap  mb-2" >
                this photo looks great
              </p>
              <p className="btn btn-success rounded  btn-sm px-4 mb-2">$ 5</p>
              <div className="row">
                <div className="col-12">
                 {showAddtoCart(addtoCart)}
                </div>
                <div className="col-12">
                  {showRemoveFromCart(removeFromcart)}
                </div>
              </div>
            </div>
          </div>
        );
      };






    export default Card;
