import React , {useState,useEffect} from  "react";
import { Redirect } from "react-router-dom";
import {AddItemTocart, removeItemFromCart} from "./helper/CartHelper";
import ImageHelper from "./helper/ImageHelper";




    const Card = ({product  ,addtoCart =true, removeFromcart=false ,setReload , reload=undefined}) => {

          const [redirect , setRedirect] = useState(false);
          const [count , setCount] = useState(product.count);


        const cardTitle = product ? product.name : "A Photo from Pixel";
        const cardDescription = product ? product.description : "default description";
        const cardPrice = product ? product.price : "default";


        const addToCart = () => {
          AddItemTocart(product , () => setRedirect(true))
        }
        const getARedirect = (redirect) => {
          if(redirect){
            return <Redirect to="/cart" />
          }
        }

        //show add to cart button
       const showAddtoCart = addtoCart => {
           return(
               addtoCart && (
                <button
                onClick={addToCart}
                className="btn btn-block btn-outline-success  mb-2" style={{width:"100%"}}
              >
                Add to Cart
              </button>
               )
           )
       }

       //show remove from cart button
       const showRemoveFromCart = removeFromcart => {
        return (
            removeFromcart && (
            <button
                    onClick={() => {removeItemFromCart(product._id);
                      setReload(true)
                    }}
                    className="btn btn-block btn-outline-danger  " style={{width:"100%"}}
                  >
                    Remove from cart
                  </button>
        )
        );
    }


        return (
          <div className="card text-white bg-dark border border-info ">
            <div className="card-header lead">{cardTitle}</div>
            <div className="card-body">
              {getARedirect(redirect)}
              <ImageHelper product={product}/>
              <p className="lead bg-success text-center font-weight-normal text-wrap  mb-2" >
                {cardDescription}
              </p>
              <p className="btn btn-success rounded  btn-sm px-4 mb-2">$ {cardPrice}</p>
              <div className="row">
                <div className="col-12">
                 {showAddtoCart(addtoCart)}
                </div>
                <div className="col-12">
                  {showRemoveFromCart(removeFromcart)}
                </div>
              </div>
            </div>
          </div>
        );
      };






    export default Card;
