import React , {useState} from 'react';
import { Link } from 'react-router-dom';
import { isAuthenticated } from '../auth/helper';
import Base from '../core/Base';
import {CreateCategory} from './helper/adminapicall'

const AddCategory = () => {
    const [name , setName] = useState ();
    const [error ,setError ] = useState();
    const [success , setSuccess] = useState(false);


    const {user,token} = isAuthenticated();

    const goBack = () => (
        <div className="mt-5"> <Link className ="btn btn-sm btn-success mb-3" to="/admin/dashboard">Admin Home</Link></div>
    );


    const handleChange = event => {
        setError("");//empty old errors
        setName(event.target.value);
    };

    const onSubmit = event => {
        event.preventDefault();
        setError("");
        setSuccess(false);

        //backend request fired
        CreateCategory(user._id , token , {name})
        .then(data => {
            if(data.error ){
                setError(true);
            }else{
                setError("");
                setSuccess(true);
                setName("");
            }
        });
    };
    const successMessage = () => {
        if (success) {
          return <h4 className="text-success">Category created successfully</h4>;
        }
      };

      const warningMessage = () => {
        if (error) {
          return <h4 className="text-success">Failed to create category</h4>;
        }
      };

    const myCategoryForm = () => (
        <form>
            <div className="form-group">
                <p className="lead">Enter the category</p>
                <input  className=" form-control my-3"
                onChange={ handleChange}
                // onChange={(e) => console.log(e)}
                value={name}
                autoFocus required placeholder="for Ex. summer"/>
                <button onClick={onSubmit} className = "btn btn-outline-info"> Create Category</button>
            </div>
        </form>
    );

    return(
        <Base title="Create a category here" description="Add a new category for products " className="container bg-success p-4">
            <div className="row bg-white rounded">
                <div className="col-md-8 offset-md-2">

                    {myCategoryForm()}{goBack()}
                </div>
            </div>
        </Base>
    );
};



export default AddCategory;
